import React, { useState } from "react";
import './App.css';
import CalculatorButtons from "./components/CalculatorButtons";
import calculate from "./logic/calculate";

function App() {
	const [result, setResult] = useState(0);

	const ac = () => setResult(calculate(result, "AC"));
	const negativeNumber = () => setResult(calculate(result, "+/-"));
	const perCent = () => setResult(calculate(result, "%"));
	const divide = () => setResult(calculate(result, "รท"));
	const mult = () => setResult(calculate(result, "mult"));

	return (
		<div className="App">
			<header className="App-header">
				<output className="result">{result}</output>
				<CalculatorButtons
					ac={ac}
					negativeNumber={negativeNumber}
					perCent={perCent}
					divide={divide}
					numberSeven="7"
					numberEight="8"
					numberNine="9"

				/>
			</header>
		</div>
	);
}

export default App;
